    public function actionUpdate($authKey, $id)
    {
        \Yii::$app->response->format = Response::FORMAT_JSON;
        $isGame = Games::find()->where(['id' => Constants::THAISHARED, 'gameAuthKey' => $authKey])->count();
        if (!$isGame) {
            return ['message' => Yii::t('app', 'Auth Game Not Found')];
        }
        $thaiSharedGame = ThaiSharedGame::find()->where(['id' => $id])->one();
        if (!$thaiSharedGame) {
            return ['message' => Yii::t('app', 'Not Found')];
        }
        $thaiSharedGame->status = 3;
        if (!$thaiSharedGame->save()) {
            return ['message' => Yii::t('app', 'Can not update thai shared game')];
        }
        return ['message' => Yii::t('app','success')];
    }

