    public function actionCloseButtonGame($authKey, $title)
    {
        \Yii::$app->response->format = Response::FORMAT_JSON;
        $isGame = Games::find()->where(['id' => Constants::THAISHARED, 'gameAuthKey' => $authKey])->count();
        if (!$isGame) {
            return ['message' => Yii::t('app', 'Auth Game Not Found')];
        }
        $date = date('Y-m-d');
        $thaiSharedGame = ThaiSharedGame::find()->where(['title' => $title, 'DATE(endDate)' => $date])->one();
        /* @var $thaiSharedGame ThaiSharedGame */
        if (!$thaiSharedGame) {
            return ['message' => 'thai shard game not found'];
        }
        $thaiSharedGame->disabled = 0;
        if (!$thaiSharedGame->save()) {
            return ['message' => 'can not save thai shard game'];
        }
        return ['message' => 'success'];
    }

    public function actionGovernment($authKey, $startDate, $endDate, $status)
    {
        \Yii::$app->response->format = Response::FORMAT_JSON;
        $game = Games::find()->where(['title' => ['หวยรัฐบาลไทย', 'หวยรัฐบาลไทย แบบมีส่วนลด'],'gameAuthKey' => $authKey])->one();
        if (!$game) {
            return ['message' => Yii::t('app', 'Auth Game Not Found')];
        }
        $model = new ThaiSharedGame();
        $model->startDate = date('Y-m-d H:i:s', strtotime($startDate));
        $model->endDate = date('Y-m-d H:i:s', strtotime($endDate));
        $model->status = $status;
        $model->description = '';
        $model->disabled = 1;
        $model->title = $game->title;
        $model->gameId = $game->id;
        $model->createdBy = Constants::user_system_id;
        $model->typeSharedGameId = 4;
        if (!$model->save()) {
            return ['message' => 'can not create game government'];
        }
        return ['message' => 'success'];
    }
